-- ============================================
-- SECURITY MASTER DATABASE SETUP
-- ============================================

USE ROLE ACCOUNTADMIN;

-- Create database and schema
CREATE DATABASE IF NOT EXISTS SECURITY_MASTER_DB;
USE DATABASE SECURITY_MASTER_DB;
CREATE SCHEMA IF NOT EXISTS SECURITIES;
USE SCHEMA SECURITIES;

-- ============================================
-- S&P 500 COMPANIES TABLE
-- ============================================

CREATE OR REPLACE TABLE SP500 (
    SYMBOL VARCHAR(10) PRIMARY KEY,
    SECURITY_NAME VARCHAR(200),
    GICS_SECTOR VARCHAR(100),
    GICS_SUB_INDUSTRY VARCHAR(100),
    HEADQUARTERS VARCHAR(200),
    DATE_ADDED DATE,
    CIK VARCHAR(20),
    FOUNDED VARCHAR(20)
);

